mapper.insert:
insert into users (id, name, age
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{age,jdbcType=INTEGER}
  )

mapper.update:
update users
set name = #{name,jdbcType=VARCHAR},
  age = #{age,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, age
from users
where id = #{id,jdbcType=INTEGER}
